import React, { Component } from "react";
import { Grid ,Typography } from "@material-ui/core";
import BlogCard from "./BlogCard"
import googleplayimage from "../../assets/googleplay-icon.svg";
import appstoreimage from "../../assets/appstore-icon.svg";

class BlogPage extends Component{
  constructor(props) {
    super(props)
    this.state = {
      data:[]
    }
  }
  componentDidMount() {
    var url="https://api.github.com/gists/b4b9341c66ec7fad324966677c656ba4";
      fetch(url)
      .then(response => {
        return response.json();
      })
      .then(d => {
        this.setState({data : JSON.parse(d.files["mdg_blogs"].content)});
        console.log(this.state.data)
      })
  }
  render(){
        return(
          <div>
          <Grid container spacing={8} style={{ padding: "8px" }}>
          <Grid item xs={12}>
            <h1 style={{ textAlign: "left" , fontSize: '40px', marginBottom: '60px' }}>Blogs</h1>
          </Grid>

            {/*
           *
           *
           * 
           * 
           *Blogs Cards
           *
           * 
           * 
           * 
           */}

          {this.state.data.map((obj, i) => (
            <Grid item sm={6} xs={12} key={i}>

                <BlogCard
                  image={require("../../assets/blogs/images/"+obj.image)}
                  title={obj.title}
                  date={obj.date}
                  content={obj.desc}
                  author={obj.author}
                  link={obj.link}
                />

            </Grid>
          ))}

          </Grid>
          {/*
           *
           *
           *  Links
           *
           *
           */}
          <Grid item xs={12}>
          <Typography variant="caption" gutterBottom style={{ textAlign: "center", fontSize: "14px",marginTop:"60px" }}>
              Find Mobile Development Groupâ€™s apps on {" "}
            </Typography>
          </Grid>

        <div style={{ textAlign: "center",marginBottom:"40px",marginTop:"10px" }}>
        <a style={{ textDecoration: 'none',color:'#FFFFFF' }} target="_blank" href="https://play.google.com/store/apps/developer?id=MDG,+SDS">
          <img
            src={googleplayimage}
            alt={"Google Play"}
            style={{ marginRight: "4px" }}
          />
          </a>
          <a style={{ textDecoration: 'none',color:'#FFFFFF' }} target="_blank" href="https://apps.apple.com/us/developer/kush-taneja/id1177582987">
          <img
            src={appstoreimage}
            alt={"App Store"}
            style={{ marginLeft: "4px" }}
          />
          </a>
        </div>

      </div>
        );
    }
}

export default BlogPage;
